        canvas.draw();
        var currentValue = minSize + (maxSize - minSize) * ((xPos - padding) / (maxSize - minSize));
        onSizeUpdate(currentValue, linePlot);
      }
    });
  });

  function onSizeUpdate(currentSize, plot) {
    plot.updateConfig({
      width: currentSize,
      height: currentSize
    });
    plot.render();
  }
});

