import { Line } from '@antv/g2plot';

fetch('../data/sales.json')
  .then((res) => res.json())
  .then((data) => {
    const linePlot = new Line(document.getElementById('container'), {
      title: {
        visible: true,
        text: 'ä¸ºæŠ˜çº¿æ·»åŠ ç¼©ç•¥è½´äº¤äº’',
