      description: {
        visible: true,
        text: 'ç¼©ç•¥è½´ (slider) äº¤äº’é€‚ç”¨äºæŠ˜çº¿æ•°æ®è¾ƒå¤šï¼Œç”¨æˆ·å¸Œæœ›å…³æ³¨æ•°æ®é›†ä¸­æŸä¸ªç‰¹æ®ŠåŒºé—´çš„åœºæ™¯ã€‚',
      },
      forceFit: true,
      padding: 'auto',
      data,
      xField: 'åŸå¸‚',
      xAxis: {
        visible: true,
        autoHideLabel: true,
      },
      yField: 'é”€å”®é¢',
      yAxis: {
        label: {
          // æ•°å€¼æ ¼å¼åŒ–ä¸ºåƒåˆ†ä½
          formatter: (v) => `${v}`.replace(/\d{1,3}(?=(\d{3})+$)/g, (s) => `${s},`),
